import json, os, re, glob

SOL_DIR = "/Users/yaronamor/Documents/yaronamor-vault/sol"
LOG_DIR = "/Users/yaronamor/.claude/projects/-Users-yaronamor-Documents-yaronamor-vault-sol"

# Get all session logs
all_logs = sorted\(glob.glob\(os.path.join\(LOG_DIR, "*.jsonl"\)\), key=os.path.getmtime, reverse=True\)

targets = [
    'A-agents/copywriter-agent.md',
    'A-agents/researcher-agent.md', 
    'A-agents/cto-agent.md',
    'A-agents/producer-agent.md',
    'A-agents/board-agent.md',
    'B-brain/references/writing-samples/sample-01.md',
    'B-brain/references/writing-samples/sample-02.md',
    'B-brain/references/writing-samples/sample-03.md',
]

def strip_line_numbers\(text\):
    lines = text.split\('\\n'\)
    cleaned = []
    for line in lines:
        m = re.match\(r'^\\s*\\d+[\\tâ†’]\(.*\), line\)
        if m:
            cleaned.append\(m.group\(1\)\)
        else:
            cleaned.append\(line\)
    return '\\n'.join\(cleaned\)

found = {}

for log_path in all_logs:
    log_name = os.path.basename\(log_path\)
    if all\(t in found for t in targets\):
        break

    with open\(log_path, 'r', errors='replace'\) as f:
        pending_reads = {}
        for line in f:
            try:
                obj = json.loads\(line\)
            except:
                continue
            msg = obj.get\('message', {}\)
            content = msg.get\('content', []\)
            if isinstance\(content, list\):
                for item in content:
                    if not isinstance\(item, dict\):
                        continue
                    if item.get\('type'\) == 'tool_use' and item.get\('name'\) == 'Read':
                        tool_id = item.get\('id', ''\)
                        fp = item.get\('input', {}\).get\('file_path', ''\)
                        if fp.startswith\(SOL_DIR\):
                            rel = fp[len\(SOL_DIR\)+1:]
                            if rel in targets and rel not in found:
                                pending_reads[tool_id] = rel
                    if item.get\('type'\) == 'tool_result':
                        tool_id = item.get\('tool_use_id', ''\)
                        if tool_id in pending_reads:
                            rel = pending_reads[tool_id]
                            result = item.get\('content', ''\)
                            if isinstance\(result, str\) and len\(result\) > 50:
                                found[rel] = strip_line_numbers\(result\)
                                print\(f"Found {rel} in {log_name} \({len\(result\)} chars\)"\)

# Write found files
for rel, content in found.items\(\):
    full_path = os.path.join\(SOL_DIR, rel\)
    os.makedirs\(os.path.dirname\(full_path\), exist_ok=True\)
    with open\(full_path, 'w'\) as f:
        f.write\(content\)
    print\(f"Wrote: {rel}"\)

missing = [t for t in targets if t not in found]
if missing:
    print\(f"\\nStill missing: {missing}"\)
else:
    print\("\\nAll target files recovered!"\)
PYEOF)
